document.getElementById("nameForm").addEventListener("submit",(function(e){e.preventDefault();var t=document.getElementById("firstName").value,n=document.getElementById("lastName").value;fetch("/greet?firstName=".concat(t,"&lastName=").concat(n)).then((function(e){return e.json()})).then((function(e){console.log(e),document.getElementById("result").innerText="Hello, ".concat(e.firstName," ").concat(e.lastName,"!")})).catch((function(e){return console.error("Error:",e)}))}));